mapper.select:
SELECT c.id,c.user_id,c.mobile, u.user_name , (SELECT IFNULL(sum(product_amount),0) from tcim_fee where profit_cfplanner_id=c.user_id and fee_type='1001') totalSales, (SELECT count(id) from tcim_fee where profit_cfplanner_id=c.user_id and fee_type='1001') countSales, (SELECT IFNULL(sum(fee_amount),0) from tcim_fee where profit_cfplanner_id=c.user_id and fee_type='1001') fee, (SELECT IFNULL(sum(fee_amount),0) from tcim_fee where profit_cfplanner_id=c.user_id and fee_type='1002') allowance, (SELECT IFNULL(sum(fee_amount),0) from tcim_fee where profit_cfplanner_id=c.user_id and fee_type='1008') activityReward, (SELECT IFNULL(sum(invest_amt),0) from tcim_product_invest_record where user_id=c.user_id and status=1) currInvestAmount from tcrm_cfplanner c left join tcrm_user_info u on u.user_id=c.user_id where 1=1
and c.mobile = #{query.name} or u.user_name =#{query.name}
order by c.id
