 select c.city,c.user_name userName,c.user_id,c.mobile,c.create_time createTime, t3.customerCount,
	IFNULL(t1.history_sales,0) as totalSales, IFNULL(t1.history_fee,0) as totalfee,
	IFNULL(t2.this_month_sales,0) as thisMonthSales,IFNULL(t2.this_month_fee,0) as thisMonthtotalfee
	 from 
	(
	select a.city,u.user_name,c.user_id,c.mobile,c.create_time from tcrm_cfplanner c
	LEFT JOIN tcrm_user_info u on u.user_id=c.user_id
	LEFT JOIN  tsys_mobile_area a on a.mobile = left(c.mobile,7)
	where c.sales_org_id=#{query.salesOrgId} 
	) c
	LEFT JOIN 
	(
	SELECT f.profit_cfplanner_id, sum(f.product_amount) as history_sales ,sum(fee_amount) as history_fee from tcim_fee f LEFT JOIN tcrm_cfplanner c on f.profit_cfplanner_id=c.user_id 
	where f.fee_type='1001' and c.sales_org_id=#{query.salesOrgId} 
	GROUP BY f.profit_cfplanner_id
	) t1
	on c.user_id=t1.profit_cfplanner_id
	LEFT JOIN 
	(
	SELECT f.profit_cfplanner_id, sum(f.product_amount) as this_month_sales ,sum(fee_amount) as this_month_fee from tcim_fee f LEFT JOIN tcrm_cfplanner c on f.profit_cfplanner_id=c.user_id 
	where f.fee_type='1001' and date_format(f.create_time, '%Y-%m')=date_format(now(),'%Y-%m')  and c.sales_org_id=#{query.salesOrgId} 
	GROUP BY f.profit_cfplanner_id
	) t2
	on c.user_id=t2.profit_cfplanner_id
	LEFT JOIN
	(
	SELECT c.user_id, count(i.id) customerCount from  tcrm_investor i LEFT JOIN tcrm_cfplanner c on i.cfplanner=c.user_id where c.sales_org_id=#{query.salesOrgId}  
	GROUP BY c.user_id 
	)t3
	on c.user_id=t3.user_id

 	1=1

 		and c.user_name  = #{query.nameOrmobile} or c.mobile =#{query.nameOrmobile} 


		and c.city = #{query.city}

SELECT a.city, (select `name` from tcim_org_info where org_number= (select org_number from tcim_product where product_id=f.product_id)) platform, uc.user_name as cfpName,uc.mobile as cfpMobile, ui.user_name as investorName,ui.mobile as investorMobile, f.product_amount investAmount,f.create_time investTime,f.fee_amount salesCost from tcim_fee f LEFT JOIN tcrm_cfplanner c on f.profit_cfplanner_id=c.user_id LEFT JOIN tsys_mobile_area a on a.mobile = left(c.mobile,7) LEFT JOIN tcrm_user_info uc on uc.user_id=f.profit_cfplanner_id LEFT JOIN tcrm_user_info ui on ui.user_id=f.investor_id LEFT JOIN tcim_product p on p.product_id=f.product_id
1=1 and f.fee_type='1001' and c.sales_org_id=#{query.salesOrgId}
and uc.user_name = #{query.nameOrmobile} or uc.mobile =#{query.nameOrmobile}
and a.city = #{query.city}
and p.org_number= #{query.platform}
and DATE_FORMAT(f.create_time, '%Y-%m-%d') >= DATE_FORMAT(#{query.startTimeForSearch}, '%Y-%m-%d')
and DATE_FORMAT(f.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{query.endTimeForSearch}, '%Y-%m-%d')
order by f.create_time desc

SELECT ifnull(sum(f.product_amount),0)/10000 totalAmt, ifnull(sum(f.fee_amount),0)/10000 totalFeeAmt, ifnull(sum(f.yearpur_amount),0)/10000 totalYearpurAmt, count(1) investCount, ifnull(sum(fd.stockYearpurAmt),0)/10000 as stockYearpurAmt from tcim_fee f LEFT JOIN tcrm_cfplanner c on f.profit_cfplanner_id=c.user_id LEFT JOIN tsys_mobile_area a on a.mobile = left(c.mobile,7) LEFT JOIN tcrm_user_info uc on uc.user_id=f.profit_cfplanner_id LEFT JOIN tcrm_user_info ui on ui.user_id=f.investor_id LEFT JOIN tcim_product p on p.product_id=f.product_id LEFT JOIN (
SELECT SUM(a.yearpur_amount) stockYearpurAmt,a.bill_id FROM `tcim_feedetail` a JOIN (SELECT MIN(f.create_time) time,f.bill_id FROM `tcim_feedetail` f LEFT JOIN tcim_product p ON f.product_id = p.product_id WHERE p.is_fixed_deadline = 2 AND f.fee_amount > 0 AND f.fee_type='1001' GROUP BY f.bill_id)b ON a.bill_id = b.bill_id AND a.create_time<>b.time
GROUP BY a.bill_id
) fd ON fd.bill_id = f.bill_id
1=1 and f.fee_type='1001' and c.sales_org_id=#{query.salesOrgId}
and uc.user_name = #{query.nameOrmobile} or uc.mobile =#{query.nameOrmobile}
and a.city = #{query.city}
and p.org_number= #{query.platform}
and DATE_FORMAT(f.create_time, '%Y-%m-%d') >= DATE_FORMAT(#{query.startTimeForSearch}, '%Y-%m-%d')
and DATE_FORMAT(f.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{query.endTimeForSearch}, '%Y-%m-%d')

SELECT count(DISTINCT(c.user_id)) from tcim_fee f LEFT JOIN tcrm_cfplanner c on f.profit_cfplanner_id=c.user_id LEFT JOIN tcrm_user_info uc on uc.user_id=f.profit_cfplanner_id
1=1 and f.fee_type='1001' and c.sales_org_id=#{query.salesOrgId}
and uc.user_name = #{query.nameOrmobile} or uc.mobile =#{query.nameOrmobile}
and a.city = #{query.city}
and p.org_number= #{query.platform}
and DATE_FORMAT(f.create_time, '%Y-%m-%d') >= DATE_FORMAT(#{query.startTimeForSearch}, '%Y-%m-%d')
and DATE_FORMAT(f.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{query.endTimeForSearch}, '%Y-%m-%d')

  	SELECT IFNULL(znh,0)/10000 znh,IFNULL(gdnh,0)/10000 gdnh ,IFNULL(gdinvest,0)/10000 gdinvest  FROM 
		(
		SELECT
		  SUM(f.yearpur_amount) gdnh,SUM(f.product_amount) gdinvest
		FROM
		  tcrm_cfplanner c
		LEFT JOIN tcrm_user_info u ON c.user_id = u.user_id
		LEFT JOIN tsys_mobile_area m ON m.mobile = LEFT(c.mobile,7)
		LEFT JOIN tcim_fee f ON c.user_id = f.profit_cfplanner_id
		AND f.fee_type = '1001'
		AND DATE(f.create_time) BETWEEN #{query.startTimeForSearch}
		AND #{query.endTimeForSearch}
		AND f.fee_amount > 0

			c.sales_org_id = #{query.salesOrgId}

				AND m.city = #{query.city} 


				AND f.product_org_id = #{query.platform}


				AND (u.mobile = #{query.nameOrmobile} OR u.user_name= #{query.nameOrmobile})


		) a,
		(
		SELECT
		 SUM(f.yearpur_amount) znh
		FROM
		  tcrm_cfplanner c
		LEFT JOIN tcrm_user_info u ON c.user_id = u.user_id
		LEFT JOIN tsys_mobile_area m ON m.mobile = LEFT(c.mobile,7)
		LEFT JOIN  tcim_feedetail f ON c.user_id = f.profit_cfplanner_id
		AND f.fee_type = '1001'
		AND DATE(f.create_time) BETWEEN #{query.startTimeForSearch}
		AND #{query.endTimeForSearch}
		AND f.fee_amount > 0

			c.sales_org_id = #{query.salesOrgId}

				AND m.city = #{query.city} 


				AND f.product_org_id = #{query.platform}


				AND (u.mobile = #{query.nameOrmobile} OR u.user_name= #{query.nameOrmobile})


		) b

SELECT IFNULL(znh,0)/10000 znh,IFNULL(gdnh,0)/10000 gdnh ,IFNULL(gdinvest,0)/10000 gdinvest FROM ( SELECT SUM(f.yearpur_amount) gdnh,SUM(f.product_amount) gdinvest FROM tcrm_cfplanner c LEFT JOIN tcrm_user_info u ON c.user_id = u.user_id LEFT JOIN tsys_mobile_area m ON m.mobile = LEFT(c.mobile,7) LEFT JOIN tcim_fee f ON c.user_id = f.profit_cfplanner_id AND f.fee_type = '1001' AND DATE(f.create_time) BETWEEN date_sub(date_sub(date_format(now(),'%y-%m-%d'),interval extract( day from now())-1 day),interval 1 month) AND date_sub(date_sub(date_format(now(),'%y-%m-%d'),interval extract( day from now()) day),interval 0 month) AND f.fee_amount > 0
c.sales_org_id = #{salesOrgId}
) a, ( SELECT SUM(f.yearpur_amount) znh FROM tcrm_cfplanner c LEFT JOIN tcrm_user_info u ON c.user_id = u.user_id LEFT JOIN tsys_mobile_area m ON m.mobile = LEFT(c.mobile,7) LEFT JOIN tcim_feedetail f ON c.user_id = f.profit_cfplanner_id AND f.fee_type = '1001' AND DATE(f.create_time) BETWEEN date_sub(date_sub(date_format(now(),'%y-%m-%d'),interval extract( day from now())-1 day),interval 1 month) AND date_sub(date_sub(date_format(now(),'%y-%m-%d'),interval extract( day from now()) day),interval 0 month) AND f.fee_amount > 0
c.sales_org_id = #{salesOrgId}
) b
