mapper.select:
SELECT i.id,i.user_id,i.mobile, u.user_name ,i.rect_invest_time, (SELECT IFNULL(sum(invest_amt),0) from tcim_product_invest_record where user_id=i.user_id ) totalInvestAmount, (SELECT IFNULL(sum(invest_amt),0) from tcim_product_invest_record where user_id=i.user_id and `status`=1) currInvestAmount, (SELECT count(1) from tcim_product_invest_record where user_id=i.user_id ) investCount, (SELECT IFNULL(sum(profit),0) from tcim_product_repayment_record where user_id=i.user_id and `status`=3) totalProfit, (SELECT IFNULL(sum(money),0) from tact_redpacket_use_record where user_id=i.user_id) as hongbaoAmount, (SELECT IFNULL(sum(fee_amount),0) from tcim_org_fee_gather where user_id=i.user_id) as saleProfix, 0 as otherReward from tcrm_investor i left join tcrm_user_info u on u.user_id=i.user_id where 1=1
and i.mobile = #{query.name} or u.user_name =#{query.name}
order by i.id

SELECT p.product_name, p.is_fixed_deadline, p.dead_line_min_value, p.dead_line_max_value, p.dead_line_min_self_defined, p.dead_line_max_self_defined, p.flow_min_rate, p.flow_max_rate, p.is_flow, (SELECT IFNULL(sum(fee_amount),0) from tcim_org_fee_gather where invest_id=r.invest_id) as saleProfix, o.name as platfromName, r.* from tcim_product_invest_record r LEFT JOIN tcim_product p on r.product_id=p.product_id LEFT JOIN tcim_org_info o on r.platfrom=o.org_number where 1=1 and r.user_id=#{query.userId}
and p.product_name = #{query.name}
and r.status = #{query.status}
and DATE_FORMAT(r.biz_time, '%Y-%m-%d') >= DATE_FORMAT(#{query.startTimeForSearch}, '%Y-%m-%d')
and DATE_FORMAT(r.biz_time, '%Y-%m-%d') <= DATE_FORMAT(#{query.endTimeForSearch}, '%Y-%m-%d')
order by r.id desc
