mapper.select:
SELECT IFNULL(SUM(a.fee_amount),0) FROM tcim_fee a WHERE a.profit_cfplanner_id = #{userId}
and a.fee_type=#{profitType}
and year(a.create_time)=year(#{date})
and year(a.create_time)=year(#{date}) and QUARTER(a.create_time)=QUARTER(#{date})
and DATE_FORMAT(a.create_time,'%Y-%m')=DATE_FORMAT(#{date},'%Y-%m')
and DATE_FORMAT(a.create_time,'%Y-%m-%d')=DATE_FORMAT(#{date},'%Y-%m-%d')

SELECT SUM(f.fee_amount) amt, cu.mobile mobile, cu.user_name name, a.city FROM tcim_feedetail f LEFT JOIN tcrm_user_info cu ON f.profit_cfplanner_id = cu.user_id LEFT JOIN tsys_mobile_area a ON a.mobile = LEFT (cu.mobile, 7) WHERE f.create_time BETWEEN #{start} AND #{end}
AND f.product_org_id=#{org}
AND f.fee_amount>0 GROUP BY f.profit_cfplanner_id ORDER BY amt DESC LIMIT 100

SELECT COUNT(f.profit_cfplanner_id) FROM ( SELECT IFNULL(SUM(f.fee_amount), 0) amt, f.profit_cfplanner_id FROM tcim_fee f WHERE f.create_time BETWEEN #{start} AND #{end}
AND f.product_org_id=#{org}
GROUP BY f.profit_cfplanner_id ) f

SELECT f.amt, COUNT(f.profit_cfplanner_id) count FROM ( SELECT IFNULL(SUM(f.fee_amount), 0) amt, f.profit_cfplanner_id FROM tcim_fee f WHERE f.create_time BETWEEN #{start} AND #{end}
AND f.product_org_id=#{org}
GROUP BY f.profit_cfplanner_id ) f GROUP BY f.amt
