mapper.select:
select 

from tcim_product_invest_record
where  id = #{0}

SELECT t.invest_id, t.invest_record_no, t.user_id, t.product_id, t.product_fee_rate, t.invest_amt, t.platfrom, t.start_time, t.is_first_invest, t.is_platfrom_first_invest FROM tcim_product_invest_record t WHERE t.product_id
#{productId}
AND t.status <> 3

SELECT ii.user_id uid, ii.user_name uname, ii.mobile umobile, ci.user_id cid, ci.user_name cname, ci.mobile cmobile, p.product_id productId, p.is_redemption deadlineType, o.org_number platfrom, o.org_fee_type model, i.invest_amt investAmt, o.org_amount_limit amtLimit, i.is_first_invest isFirstInvest, i.is_platfrom_first_invest isPlatfromFirstInvest FROM tcim_product_invest_record i LEFT JOIN tcrm_investor iv ON i.user_id = iv.user_id LEFT JOIN tcrm_user_info ii ON i.user_id = ii.user_id LEFT JOIN tcrm_cfplanner c ON iv.cfplanner = c.user_id LEFT JOIN tcrm_user_info ci ON c.user_id = ci.user_id LEFT JOIN tcim_product p ON i.product_id = p.product_id LEFT JOIN tcim_org_info o ON i.platfrom = o.org_number WHERE
DATE_FORMAT(i.end_time,'%Y-%m-%d %H:%i')=#{today}
AND o.org_fee_type = 2 AND i.`status` = 3 AND p.is_redemption <> 0
