mapper.select:
SELECT IFNULL(SUM(f.yearpur_amount),0)/10000 yearpurAmount, IFNULL(sum(f.product_amount),0)/10000 productAmount, u.user_name userName, u.mobile, cu.mobile cfpMobile, cu.user_name cfpName, a.city cfpCity FROM tcim_fee f LEFT JOIN tcrm_user_info u ON f.investor_id = u.user_id LEFT JOIN tcrm_user_info cu ON f.profit_cfplanner_id = cu.user_id LEFT JOIN tsys_mobile_area a ON a.mobile = LEFT (cu.mobile, 7) WHERE f.create_time BETWEEN #{start} AND #{end}
AND f.product_org_id=#{org}
AND f.fee_type=1001 GROUP BY f.investor_id ORDER BY yearpurAmount DESC LIMIT 100

SELECT COUNT(f.investor_id) FROM ( SELECT IFNULL(SUM(f.yearpur_amount), 0) amt, f.investor_id FROM tcim_fee f WHERE f.fee_type=1001 and f.create_time BETWEEN #{start} AND #{end}
AND f.product_org_id=#{org}
GROUP BY f.investor_id ) f

SELECT f.amt, COUNT(f.investor_id) count FROM ( SELECT IFNULL(SUM(f.yearpur_amount), 0) amt, f.investor_id FROM tcim_fee f WHERE f.fee_type=1001 and f.create_time BETWEEN #{start} AND #{end}
AND f.product_org_id=#{org}
GROUP BY f.investor_id ) f GROUP BY f.amt
