mapper.select:
SELECT IFNULL(sum(product_amount),0) totalAmount, IFNULL(sum(fee_amount),0) totalProfit, IFNULL(sum(allowance),0) allowance,IFNULL(sum(childrenAllowance),0) childrenAllowance, count(1) as salesCount from ( SELECT case when f.fee_rate=25 then f.fee_amount end as allowance, case when f.fee_rate=5 then f.fee_amount end as childrenAllowance, f.* from tcim_fee f where fee_type='1002' and f.profit_cfplanner_id=#{userId,jdbcType=VARCHAR}
and date_format(f.create_time, '%Y-%m')=#{date}
and YEARWEEK(date_format(f.create_time,'%Y-%m-%d')) = YEARWEEK(#{date})
and date_format(f.create_time, '%Y-%m-%d')=#{date}
)t

SELECT u.user_name userName,u.mobile, c.head_image headImage, f.create_time bizTime, p.product_name productName, f.product_amount saleAmount, f.fee_amount feeAmount,f.fee_rate feeRate, case when fee_rate=25 then '1' else '2' end type from tcim_fee f LEFT JOIN tcrm_user_info u on f.origin_cfplanner_id=u.user_id LEFT JOIN tcrm_cfplanner c on f.origin_cfplanner_id=c.user_id LEFT JOIN tcim_product p on p.product_id=f.product_id where f.fee_type='1002' and f.profit_cfplanner_id=#{userId,jdbcType=VARCHAR}
and date_format(f.create_time, '%Y-%m')=#{date}
and YEARWEEK(date_format(f.create_time,'%Y-%m-%d')) = YEARWEEK(#{date})
and date_format(f.create_time, '%Y-%m-%d')=#{date}
order by f.id desc
