mapper.select:
SELECT p.product_name productName, p.is_flow isflow, p.flow_min_rate flowMinRate, p.flow_max_rate flowMaxRate, a.invest_amt investAmt, a.profit, a.start_time startTime, a.end_time endTime, a.status status, c.fee_amount feeAmt FROM tcim_product_invest_record a LEFT JOIN tcim_product p on a.product_id=p.product_id LEFT JOIN tcim_org_fee_gather c ON a.invest_id = c.invest_id WHERE a.user_id = #{userId}
AND a.platfrom = #{platfrom,jdbcType=VARCHAR}
ORDER BY a.start_time DESC

  		 SELECT
			IFNULL(SUM(i.invest_amt), 0) value
		FROM
			tcim_org_info o
		LEFT JOIN tcim_product_invest_record i ON o.org_number = i.platfrom

			 1=1

				AND o.org_number = #{platfrom}


				AND DATE_FORMAT(i.start_time,'%Y-%m-%d')     >=  #{startTime}


				AND DATE_FORMAT(i.start_time,'%Y-%m-%d')     <=  #{endTime}

SELECT IFNULL(SUM(i.invest_amt), 0) value, o.`name` FROM tcim_org_info o LEFT JOIN tcim_product_invest_record i ON o.org_number = i.platfrom
1=1
AND o.org_number = #{platfrom}
AND DATE_FORMAT(i.start_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(i.start_time,'%Y-%m-%d') <= #{endTime}
GROUP BY o.org_number ORDER BY value DESC

SELECT IFNULL(SUM(a.amt),0) amt FROM ( SELECT SUM(i.invest_amt) amt, CONCAT( p.dead_line_min_value, '-', dead_line_max_value ) deadLine FROM tcim_product p LEFT JOIN tcim_product_invest_record i ON i.product_id = p.product_id
p.org_number =#{platfrom}
AND DATE_FORMAT(i.start_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(i.start_time,'%Y-%m-%d') <= #{endTime}
GROUP BY p.product_id ) a
